import { Component } from "react";

const styles = {
    sections: {
        height: '60px',
        backgroundColor: '#7c4c46',

        display: 'flex',
    },
    ul: {
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        width: '100%',
        margin: '0 100px',
        listStyleType: 'none',
        padding:'0 100px'
    },
    a: {
        color: '#fff',
        textDecoration: 'none',
    }
}

class Sections extends Component {
    handleClick = (event) => {
        // Obtenemos el hash del enlace
        const hash = event.target.hash;

        // Obtenemos la posición de la sección a la que queremos desplazarnos
        const section = document.querySelector(hash);
        const sectionPosition = section.offsetTop;

        // Desplazamos la página a la posición de la sección, restando 200 píxeles adicionales
        window.scrollTo({
            top: sectionPosition - 200,
            behavior: 'smooth' // Desplazamiento suave
        });
    };

    render() {
        return (
            <div style={styles.sections}>
                <ul style={styles.ul}>
                    <li><a style={styles.a} href="#historia" onClick={this.handleClick}>Nuestra Historia</a></li>
                    <li><a style={styles.a} href="#productos" onClick={this.handleClick}>Nuestros productos</a></li>
                    <li><a style={styles.a} href="#ubicacion" onClick={this.handleClick}>Ubicación</a></li>
                    <li><a style={styles.a} href="#redes" onClick={this.handleClick}>Redes Sociales</a></li>
                </ul>
            </div>
        );
    }
}

export default Sections;
