import { Component } from "react";
import Button from "./Button";

const styles = {
    producto: {
        border: 'solid 0.2px #3e2623', 
        boxShadow: '-27px 25px 27px 3px rgba(0,0,0,0.1),0px 10px 15px -3px rgba(0,0,0,0.1),0px 10px 15px -3px rgba(0,0,0,0.1)',
        width: '30%',
        padding: '10px 15px',
        borderRadius: '5px',
        margin: '10px', 
        backgroundColor: '#f0e9e3',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        transition: 'transform 0.3s, box-shadow 0.3s', // Transición para suavizar los cambios
    },
    img: {
        width: '100%',
    },
    button: {
        padding: '10px 15px',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontFamily: '"Protest Riot", sans-serif',
        fontWeight: 400,
        fontStyle: 'normal',
        transition: 'transform 0.3s', // Transición para suavizar los cambios
    },
};

class Producto extends Component {
    render() {
        const { producto, agregarAlCarro } = this.props;
        return (
            <div style={styles.producto}>
                <img style={styles.img} src={producto.img} alt={producto.name} />  
                <h3>{producto.name}</h3>
                <p>Precio: ${producto.price}</p>
                <Button
                    onClick={() => agregarAlCarro(producto)}
                    style={styles.button}
                    hover={true}// Agregamos estilos al botón
                >
                    Agregar al carro
                </Button>
            </div>
        );
    }
}

export default Producto;
