import { Component } from "react";
import BubbleAlert from "./BubbleAlert";
const styles = {
    button: {
        backgroundColor: '#5b9c4c', 
        color: '#fff',
        border: 'none',
        borderRadius:'15px',
        padding:'8px 15px',
        cursor: 'pointer',
        display: 'flex',
        flexDirection:'row',
        justifyContent: 'center',
        alignItems: 'center',
        fontFamily: '"Apple Chancery", cursive',
        gap:'10px',
    },
    carro: {
        width: '30px',
    },
    bubble: {
        position: 'relative',
        left: '-15px',
        top:'58px',

    }
}

class Carro extends Component{
    render() {
        const { carro } = this.props
        const cantidad=carro.reduce((acc,el)=>acc+el.cantidad,0)
        return (
            <div>
                <span style={styles.bubble}>
                    {cantidad!==0}
                    <BubbleAlert value={cantidad} /></span>
                <button style={styles.button }><img style={styles.carro } src="/img/icons8-carrito-de-compras-50.png" alt="Carrito de compras" />Comprar</button>
            </div>
        )
    }
}
export default Carro
