import { Component } from "react";

const styles = {
    button: {
        backgroundColor: '#e84328', 
        color: '#fff',
        padding: '10px 15px',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontFamily: '"Protest Riot", sans-serif',
        fontWeight: 400,
        fontStyle: 'normal',
        transition: 'transform 0.3s', // Agregar una transición suave al cambio de tamaño
    },
    buttonHover: {
        transform: 'scale(1.16)', // Hacer el botón un 16% más grande en estado de hover
    }
};

class Button extends Component {
    render() { 
        return (
            <button 
                style={{ ...styles.button, ...(this.props.hover && styles.buttonHover) }} // Aplicar el estilo de hover si la prop hover está presente
                {...this.props}
            ></button>
        )
    }
}

export default Button;
