import { Component } from 'react'
import Productos from './components/Productos'
import Layout from './components/Layout'
import Title from './components/Title'
import Navbar from './components/Navbar'
import Sections from './components/Sections'

class App extends Component{
  state = {
    productos: [
      { name:'Café Molido 250g', price:15000, img:'/img/cafe250.jpeg'},
      { name: 'Café Molido 500g', price: 30000, img: '/img/cafe500.jpeg' },
      { name: 'Café en Grano 250g', price: 15000, img: '/img/cafe250.jpeg' },
      { name:'Café en Grano 500g', price:30000, img:'/img/cafe500.jpeg'},
    ],
    carro: [
     // { name:'Café Molido 250g', price:15000, img:'/img/cafe250.jpeg', cantidad: },
    ],
  }
agregarAlCarro = (producto) => {
  const { carro } = this.state;
  const productoExistente = carro.find(x => x.name === producto.name);

  if (productoExistente) {
    const newCarro = carro.map(x =>
      x.name === producto.name ? { ...x, cantidad: x.cantidad + 1 } : x
    );

    return this.setState({
      carro: newCarro
    });
  } else {
    return this.setState({
      carro: this.state.carro.concat({
        ...producto,
        cantidad: 1
      })
    });
  }
};
  render() {
       console.log(this.state.carro)
    return (
      <div>
        <Navbar />
        <Sections/>
        <Layout>
        <Title></Title>
        <Productos
          agregarAlCarro={this.agregarAlCarro}
          productos={this.state.productos}
          />
        </Layout>
      </div>

    )
  }
}

export default App;
