import { Component } from "react";
import Logo from "./Logo";
import Carro from "./Carro";

const styles = {
  navbar: {
    color: '#3e2623',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: '0 100px',
    position: 'sticky', 
    top: 0, 
    zIndex: 9999,
    width: '100%',
    left: 0,
    right: 0
  }
}

class Navbar extends Component {
  state = {
    visible: true // Estado inicial: navbar visible
  };

  componentDidMount() {
    window.addEventListener("scroll", this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  handleScroll = () => {
    // Verificar si el usuario está desplazándose hacia arriba o hacia abajo
    const currentScrollPos = window.pageYOffset;
    const visible = currentScrollPos < this.prevScrollPos;

    // Actualizar el estado del navbar
    this.setState({
      visible
    });

    // Guardar la posición de desplazamiento actual para la próxima comparación
    this.prevScrollPos = currentScrollPos;
  };

  handleClick = () => {
    // Ocultar el navbar cuando se hace clic
    this.setState({
      visible: false
    });
  };

  render() {
    const { carro, esCarroVisible, mostrarCarro } = this.props;
    const { visible } = this.state;
    return (
      <nav style={{...styles.navbar, display: visible ? 'flex' : 'none'}} onClick={this.handleClick}>
        <Logo />
        <Carro carro={carro} esCarroVisible={esCarroVisible} mostrarCarro={mostrarCarro} />
      </nav>
    );
  }
}

export default Navbar;
